package middlewares

import (
	"testing"

	"github.com/spruceid/siwe-go"
	"github.com/stretchr/testify/require"
)

func TestSIWE(t *testing.T) {
	t.Parallel()

	t.Run("valid", func(t *testing.T) {
		t.Parallel()

		siweToken := "" //nolint
		issuer, err := parseAuth(siweToken)
		require.NoError(t, err)
		require.Equal(t, "0xd535bAd504CDd77e2C51dE26F416693DF7a01ac8", issuer)
	})
	t.Run("wrong domain", func(t *testing.T) {
		t.Parallel()

		siweToken := "" //nolint
		_, err := parseAuth(siweToken)
		require.ErrorIs(t, err, errSIWEWrongDomain)
	})
	t.Run("expired", func(t *testing.T) {
		t.Parallel()

		siweToken := "" //nolint
		_, err := parseAuth(siweToken)
		var expErr *siwe.ExpiredMessage
		require.ErrorAs(t, err, &expErr)
	})
}
